/* SET-UP */
@import url("https://fonts.googleapis.com/css?family=Roboto|Gelasio&display=swap");
$color-logo: #ffb400;

body {
  margin: 0;
  font-family: "Roboto", sans-serif;
}

a {
  text-decoration: none;
}

li {
  list-style: none;
}

.container {
  min-height: 600px;
}

h1 {
  font-size: 3em;
}

@media (max-width: 768px) {
  h1 {
    margin-top: 1.5em;
    font-size: 2.5em;
  }

  .smallerTitle {
    font-size: 2em;
  }
}

/* NAVBAR */

.navbar {
  height: 70px;
  top: 0;
  margin-top: 0;
  margin-left: 0;
  margin-right: 0;
  text-align: center;
  background-color: $color-logo;
  display: flex;
  justify-content: space-between;
  position: fixed;
  z-index: 100;
  width: 100%;
  button {
    border: 0;
    padding: 0;
    font-family: inherit;
    background: 0;
    font-weight: bold;
    &:hover {
      color: white;
    }
  }
}

.lessOpacity {
  opacity: 0.85;
}

.navbar-logo {
  height: 60px;
  margin-left: 1em;
}

.navbar-menu {
  list-style-type: none;
}

.navbar-link {
  color: black;
  &:hover {
    color: white;
    font-weight: bold;
  }
}

.fm-logo {
  width: 100px;
}

.logo-with-description {
  display: flex;
  flex-direction: row;
  color: white;
  font-family: "Gelasio", serif;
  align-items: center;
  div {
    padding-left: 0;
    margin-left: -1.6em;
    font-size: 0.8em;
  }
}

@keyframes menuslider {
  from {
    width: 0;
  }
  to {
    width: 100px;
  }
}

@media (max-width: 768px) {
  #menu {
    display: none;
    background-color: $color-logo;
    padding: 1em;
    padding-top: 2em;
    line-height: 1.5em;
    height: 180px;
    border-radius: 0px 0px 0px 25px;
    //width: 100px;
    animation-name: menuslider;
    animation-duration: 1s;
    overflow: hidden;
    li {
      margin-top: 0.6em;
      width: 100px;
    }
  }

  #navbar-icon {
    color: black;
    display: block;
    position: absolute;
    margin-top: 1em;
    margin-right: 1em;
    right: 0;
    top: 0;
    border: none;
    background: none;
    outline: none;
    div {
      width: 30px;
      height: 4px;
      background-color: black;
      margin: 4px 0;
    }
  }
}

.first-line {
  transform: translateY(8px) rotate(135deg);
}
.second-line {
  transform: rotate(-135deg);
}

@media (min-width: 769px) {
  #menu {
    display: flex !important;
    li {
      padding: 0.5em;
    }
  }
  #navbar-icon {
    display: none !important;
  }
}

/* TITLE CONTAINER */
@keyframes displaceContentHorizontally {
  from {
    transform: translateX(-800px);
  }
  to {
    transform: translateX(0px);
  } /* slide down to make room for advertisements */
}

@keyframes displaceContentVertically {
  from {
    transform: translateY(-800px);
  }
  to {
    transform: translateY(0px);
  } /* slide down to make room for advertisements */
}

@keyframes greyBackground {
  from {
   background-color: rgb(213, 212, 220, 0);
  }
  to {
    background-color: rgb(213, 212, 220, 0.3);
  } /* slide down to make room for advertisements */
}

.title-container {
  height: 640px;
  margin-top: 70px;
  display: flex;
  justify-content: flex-end;
  align-items: flex-end;
  background-color: grey;
  background-image: url("./../images/entrada-alargada.jpg");
  background-position: center; /* Center the image */
  background-repeat: no-repeat; /* Do not repeat the image */
  background-size: cover;
  h1 {
    text-align: justify;
    font-size: 2.5em;
    color: white;
    overflow: hidden;
    //font-family: "Gelasio", serif;
    font-weight: bold;
    margin: 1em;
    padding: 0.5em;
    margin-bottom: 1.5em;
    border-radius: 25px;
    width: 50%;
    animation: displaceContentHorizontally 1.5s, greyBackground 2s linear 2s;
    animation-fill-mode: forwards;
  }
  img {
    width: 30%;
  }
}


@media (max-width: 768px) {
  .title-container {
    height: 580px;
    justify-content: center;
    align-items: flex-end;
    background-position: left; /* Center the image */
    h1 {
      text-align: justify;
      font-size: 1.5em;
      margin:0.5em;
      width: 90%;
      padding-left:1em;
      padding-bottom: 0.5em;
      padding-right: 0.4em;
      padding-top:0.5em;
      margin-bottom:2.5em;
      border-radius: 25px;
      animation: displaceContentVertically 1.5s, greyBackground 2s linear 2s;
      animation-fill-mode: forwards;
    }
    img {
      width: 100%;
    }
  }
}

