/* ABOUT CONTAINER */

#about {
  padding: 2em;
  background-color: #d5d4dc;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  div {
    margin-top: 2em;
    font-size: 1em;
    line-height: 2em;
    p {
      margin: 0;
      text-align: justify;
    }
  }
}

@media (max-width: 768px) {
  #about {
    padding: 1em;
    text-align: justify;
  }
}

/* PARTNERS CONTAINER */

#partners {
  background-color: #d5d4dc;
  color: black;
  //For some reason the display flex in this container is not working in Internet Explorer so we will center its content with text-align and margin.
  // display: -webkit-flex;
  // display: -ms-flexbox;
  // display: flex;
  // flex-direction: column;
  // align-items: center;
  // justify-content: center;
  text-align: center;
  margin: auto;
  padding: 1em;
  h1 {
    padding-top: 1em;
  }
  ul {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    padding: 0;
    li {
      width: 30%;
      padding: 0.5em;
      margin: 0.5em;
      text-align: center;
    }
    li:hover {
      text-shadow: 2px 2px 4px white;
    }
  }
}

@media (max-width: 768px) {
  #partners {
    padding: 0.5em;
    h1 {
      margin-bottom: 0em;
      padding-top: 0.5em;
    }
    ul li {
      width: 38%;
    }
  }
}

/* SERVICES CONTAINER */

#services {
  margin: 1em;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.services-container {
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  flex-wrap: wrap;
  div {
    padding: 1em;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    div {
      text-align: center;
    }
  }
  img {
    max-width: 100px;
    &:hover {
      transform: scale(1.3);
      z-index: -1;
    }
  }
}

@media (max-width: 768px) {
  .services-container div {
    width: 40%;
  }
}

@media (min-width: 869px) {
  .services-container div {
    width: 15%;
  }
}

/* CLIENTS */

#clients {
  margin: 1em;
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.clients-container {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-around;
  align-items: center;
  align-content: center;
  .slides {
    margin: auto 1%;
    height: 200px;
    width: auto;
    // height: 100%;
    // height auto doesn't work on chrome. The alternative to avoid distortion is to write height: 100% or align-self: center
    // align-self: center;
    max-width: 30%;
  }
}

@media (min-width: 769px) {
  #clients {
    p {
      margin-bottom: 4em;
    }
  }
}

@media (max-width: 768px) {
  #clients {
    min-height: 600px;
    height: 100%;
    .clients-container {
      flex-direction: column;
      justify-content: center;
      //flex-wrap: wrap;
      align-items: center;
      min-height: 400px;
      height: 100%;
      // overflow: hidden;
      .slides {
        height: 100px;
        width: auto;
        max-width: none;
        margin-bottom: 0.5em;
        // overflow: hidden;
      }
    }
  }
}

/* FOOTER */

#contacts {
  left: 0;
  bottom: 0;
  width: 100%;
  background-color: black;
  color: white;
  height: 100%;
}

.footer-contents {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 1em;
  .contact-details {
    display: flex;
    flex-direction: column;
    align-self: center;
    div {
      margin-bottom: 1em;
      h6 {
        color: $color-logo;
      }
      .email-link {
        color: white;
      }
    }
  }
  .address-map {
    text-align: center;
    div {
      width: 50%;
    }
    div {
      margin-top: 1em;
    }
  }
  h6 {
    margin-top: 1em;
    margin-bottom: 0;
    font-size: 1.5em;
  }
  p {
    margin-top: 1em;
    margin-bottom: 0;
  }
  .social-logo {
    width: 25px;
  }
}
@media (min-width: 769px) {
  #map-computer {
    display: inherit;
    width: 80%;
    border-radius: 5px;
    &:hover {
      box-shadow: 0 4px 8px 0 white, 0 6px 20px 0 white;
    }
  }
  #map-mobile {
    display: none;
  }
}

@media (max-width: 768px) {
  #contacts {
    height: 100%;

    .footer-contents {
      display: flex;
      flex-direction: column;
      .contact-details {
        padding-top: 2em;
        h6 {
          font-size: 1em;
        }
      }
      div {
        width: 100%;
        #map-mobile {
          width: 90%;
          margin-top: 1em;
          display: inherit;
          border-radius: 5px;
        }
        #map-computer {
          display: none;
        }
      }
    }
  }
}
